function o(t){return new Intl.NumberFormat("en-US").format(t)}function u(t){return new Intl.NumberFormat("en-US",{style:"currency",currency:"USD",minimumFractionDigits:0,maximumFractionDigits:0}).format(t)}function m(t,n=0){return`${t.toFixed(n)}%`}function c(t){const n=new Date(t);return new Intl.DateTimeFormat("en-US",{year:"numeric",month:"short",day:"numeric"}).format(n)}function i(t){const n=new Date(t);return new Intl.DateTimeFormat("en-US",{month:"short",day:"numeric"}).format(n)}function r(t,n){return n===0?0:(t-n)/n*100}function f(t,n){const e=r(t,n);return`${e>=0?"+":""}${e.toFixed(1)}%`}function s(t){if(t!=null)return typeof t=="number"?t:t.students*t.perStudentCost}export{u as a,o as b,f as c,i as d,c as e,m as f,s as r};
